from transformers import pipeline

model = pipeline(
    task = 'question-answering', 
    model = 'timpal0l/mdeberta-v3-base-squad2')

question = '–ö–∞–∫–æ–π –≥–ª–∞–≤–Ω—ã–π –¥–µ–≤–∏–∑?'
context = '–ú–µ—á—Ç–∞ –∫–∞–∂–¥–æ–≥–æ –≤–ª–∞–¥–µ–ª—å—Ü–∞ –∫–æ–º–ø–∞–Ω–∏–∏ - –∑–∞–ø–æ–ª—É—á–∏—Ç—å —Å–∞–º–æ—Å—Ç–æ—è—Ç–µ–ª—å–Ω—É—é –¥—É–º–∞—é—â—É—é –µ–¥–∏–Ω–∏—Ü—É, –≥–æ—Ç–æ–≤–æ–≥–æ –±–æ–π—Ü–∞, –∫–æ—Ç–æ—Ä—ã–π –∑–∞–∫—Ä—ã–≤–∞–µ—Ç –ø–ª–∞—Å—Ç –∑–∞–¥–∞—á, –≤—Å–µ–≥–¥–∞ –∑–∞–º–æ—Ç–∏–≤–∏—Ä–æ–≤–∞–Ω –∏ –µ–∂–µ–º–µ—Å—è—á–Ω–æ –≤—ã–¥–∞–µ—Ç –≤—ã—Å–æ–∫–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã. –ù–∞ —Å–∞–º–æ–º –¥–µ–ª–µ –∑–¥–µ—Å—å –∏ —Å–∫—Ä—ã–≤–∞–µ—Ç—Å—è –≥–ª–∞–≤–Ω–∞—è ¬´–ª–æ–≤—É—à–∫–∞¬ª, –ø–æ—Ç–æ–º—É —á—Ç–æ —á–µ–ª–æ–≤–µ—á–µ—Å–∫–∏–π —Ñ–∞–∫—Ç–æ—Ä –∏ –æ–Ω–±–æ—Ä–¥–∏–Ω–≥ –Ω–∏–∫—Ç–æ –Ω–µ –æ—Ç–º–µ–Ω—è–ª.  üëâüèª–ù–∞–ø—Ä–∏–º–µ—Ä, –ø—Ä–µ–¥—Å—Ç–∞–≤—å—Ç–µ —Å–µ–±–µ —Å–∏—Ç—É–∞—Ü–∏—é: –ø–µ—Ä–≤—É—é –Ω–µ–¥–µ–ª—é –≤–∞—à –Ω–æ–≤—ã–π —Å–æ—Ç—Ä—É–¥–Ω–∏–∫ –∞–∫—Ç–∏–≤–Ω–æ —Ä–∞–∑–≤–∏–≤–∞–µ—Ç/–ø—Ä–æ–¥–≤–∏–≥–∞–µ—Ç/–ø—Ä–æ–¥–∞–µ—Ç –∫–∞–∫–æ–π-–ª–∏–±–æ –ø—Ä–æ–¥—É–∫—Ç, –Ω–æ –∫ –∫–æ–Ω—Ü—É –º–µ—Å—è—Ü–∞ –µ–≥–æ ¬´–∑–∞–ø–∞–ª¬ª –ø—Ä–æ—Ö–æ–¥–∏—Ç. –ó–Ω–∞–∫–æ–º–æ?  –ß–∞—â–µ –≤—Å–µ–≥–æ —ç—Ç–æ –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç –Ω–µ –ø–æ—Ç–æ–º—É, —á—Ç–æ –≤—ã –Ω–∞–Ω—è–ª–∏ –Ω–µ —Ç–æ–≥–æ —á–µ–ª–æ–≤–µ–∫–∞, –∞ –ø–æ—Ç–æ–º—É —á—Ç–æ –Ω–µ –ø—Ä–æ–≤–µ–ª–∏ –∫–∞—á–µ—Å—Ç–≤–µ–Ω–Ω—É—é –∞–¥–∞–ø—Ç–∞—Ü–∏—é –∏ –¥–µ–ª–µ–≥–∏—Ä–æ–≤–∞–ª–∏ –æ—Ç–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω–æ—Å—Ç—å –∑–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç –Ω–∞ –æ–¥–Ω–æ–≥–æ —á–µ–ª–æ–≤–µ–∫–∞, –ø–æ–ª–Ω–æ—Å—Ç—å—é –∏—Å–∫–ª—é—á–∏–≤ —Å–≤–æ–µ –≤–æ–≤–ª–µ—á–µ–Ω–∏–µ –≤ —Ä–∞–±–æ—Ç—É. –¢–æ, —á—Ç–æ –Ω–æ–≤—ã–π —Å–æ—Ç—Ä—É–¥–Ω–∏–∫ ¬´—Å–ª–∏–ª—Å—è¬ª, –≤ –¥–∞–Ω–Ω–æ–º —Å–ª—É—á–∞–µ - —ç—Ç–æ –∞–±—Å–æ–ª—é—Ç–Ω–æ –Ω–æ—Ä–º–∞–ª—å–Ω–æ. –°–∫–æ—Ä–µ–µ –≤—Å–µ–≥–æ, –æ–Ω –Ω–µ –ø–æ—á—É–≤—Å—Ç–≤–æ–≤–∞–ª –ø–æ–¥–¥–µ—Ä–∂–∫—É –∏ –≤–æ–≤–ª–µ—á–µ–Ω–∏–µ —Å –≤–∞—à–µ–π —Å—Ç–æ—Ä–æ–Ω—ã, —Ä–∞—Å—Ç–µ—Ä—è–ª—Å—è –æ—Ç –æ–≥—Ä–æ–º–Ω–æ–≥–æ –æ–±—ä–µ–º–∞ –∑–∞–¥–∞—á –∏–ª–∏, –ø—Ä–æ—â–µ –≥–æ–≤–æ—Ä—è, ¬´–≤—ã–≥–æ—Ä–µ–ª¬ª –≤ –ø–µ—Ä–≤—ã–π –º–µ—Å—è—Ü —Ä–∞–±–æ—Ç—ã.  –ü–æ–¥–±–æ—Ä –ø–µ—Ä—Å–æ–Ω–∞–ª–∞ - —ç—Ç–æ –≤—Å–µ–≥–æ –ª–∏—à—å –ø–µ—Ä–≤—ã–π —à–∞–≥ –ø—Ä–∏ –Ω–∞–π–º–µ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞. –í–∞–∂–Ω–æ –Ω–µ –∑–∞–±—ã–≤–∞—Ç—å –æ —Ç–∞–∫–∏—Ö —Å—É—â–µ—Å—Ç–≤–µ–Ω—ã—Ö —ç—Ç–∞–ø–∞—Ö, –∫–∞–∫ –æ–Ω–±–æ—Ä–¥–∏–Ω–≥ –∏ –∞–¥–∞–ø—Ç–∞—Ü–∏—è, –Ω–∞—Å—Ç–∞–≤–Ω–∏—á–µ—Å—Ç–≤–æ, –æ–±—É—á–µ–Ω–∏–µ.   –í –∫–æ–º–ø–∞–Ω–∏–∏ TOYOTA, –∫—Å—Ç–∞—Ç–∏, –≥–ª–∞–≤–Ω—ã–º –¥–µ–≤–∏–∑–æ–º —è–≤–ª—è–µ—Ç—Å—è —Ñ—Ä–∞–∑–∞ ¬´–£–≤–∏–¥–µ—Ç—å –≤—Å–µ —Å–≤–æ–∏–º–∏ –≥–ª–∞–∑–∞–º–∏¬ª –í—ã –∫–æ–≥–¥–∞-–Ω–∏–±—É–¥—å –∑–∞–¥—É–º—ã–≤–∞–ª–∏—Å—å –æ –µ–µ –∑–Ω–∞—á–µ–Ω–∏–∏ –∏ –∫–∞–∫–∞—è —Ü–µ–Ω–Ω–æ—Å—Ç—å –≤–ª–æ–∂–µ–Ω–∞ –≤ –¥–∞–Ω–Ω—ã–π –¥–µ–≤–∏–∑? –£–≤–∏–¥–µ—Ç—å –≤—Å–µ —Å–≤–æ–∏–º–∏ –≥–ª–∞–∑–∞–º–∏ - –∑–Ω–∞—á–∏—Ç, –ø–æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è –ª–∏—á–Ω–æ —Å –ø—Ä–æ–±–ª–µ–º–æ–π, –∫–æ—Ç–æ—Ä–∞—è –≤–æ–∑–Ω–∏–∫–ª–∞ —É —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞, –ø—Ä–∏—á–µ–º –Ω–µ–≤–∞–∂–Ω–æ - —Ä—É–∫–æ–≤–æ–¥–∏—Ç–µ–ª—å –æ–Ω –∏–ª–∏ —Ç–æ–ª—å–∫–æ —Å—Ç–∞–∂–µ—Ä.'

print(model(question=question, context=context))